mapper.update:
UPDATE course
                        SET id=#{course.id},
                            name=#{course.name},
                            introduce=#{course.introduce},
                            type=#{course.type}
                        WHERE id = #{course.id}

UPDATE course
SET id=#{course.id},
    name=#{course.name},
    type=#{course.type}
WHERE id = #{course.id}
